mapper.insert:
insert into xxl_job_log_report (
	trigger_day,
	running_count,
	suc_count,
	fail_count
) values (
	#{triggerDay},
	#{runningCount},
	#{sucCount},
	#{failCount}
);

mapper.update:
update xxl_job_log_report
set running_count = #{runningCount},
	suc_count = #{sucCount},
	fail_count = #{failCount}
where trigger_day = #{triggerDay}
